function renderMedia(archetypeNum) {
  const m = ARCHETYPE_MEDIA[archetypeNum];
  if (m && m.image) {
    setResultMedia({ imageSrc: m.image, caption: m.caption || "" });
  } else {
    setResultMedia(); // —Å–ø—Ä—è—Ç–∞—Ç—å –∏ —Å–±—Ä–æ—Å–∏—Ç—å
  }
}



function setResultMedia({ imageSrc = "", videoSrc = "", caption = "" } = {}) {
  // –∫—É–¥–∞ –≤—Å—Ç–∞–≤–ª—è—Ç—å –º–µ–¥–∏–∞–±–ª–æ–∫
  const textWrap = document.getElementById('result-text') || document.body;

  // –Ω–∞–π–¥—ë–º –∏–ª–∏ —Å–æ–∑–¥–∞–¥–∏–º figure + –≤–ª–æ–∂–µ–Ω–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
  let mediaWrap = document.getElementById('result-media');
  let img, video, cap;

  if (!mediaWrap) {
    mediaWrap = document.createElement('figure');
    mediaWrap.id = 'result-media';
    img = document.createElement('img');   img.id = 'result-image';  img.alt = '';
    video = document.createElement('video'); video.id = 'result-video'; video.controls = true; video.playsInline = true;
    cap = document.createElement('figcaption'); cap.id = 'result-caption';
    mediaWrap.append(img, video, cap);
    textWrap.prepend(mediaWrap); // —Å—Ç–∞–≤–∏–º –≤ –Ω–∞—á–∞–ª–æ —Ç–µ–∫—Å—Ç–∞
  } else {
    img   = mediaWrap.querySelector('#result-image');
    video = mediaWrap.querySelector('#result-video');
    cap   = mediaWrap.querySelector('#result-caption');
  }

  // —Å–±—Ä–æ—Å —Å–æ—Å—Ç–æ—è–Ω–∏—è
  img.removeAttribute('src');
  video.removeAttribute('src');
  video.load();
  cap.textContent = "";
  mediaWrap.hidden = true;
  img.style.display = 'none';
  video.style.display = 'none';

  // –µ—Å–ª–∏ –ø–æ—á–µ–º—É-—Ç–æ figure –æ–∫–∞–∑–∞–ª—Å—è –Ω–µ –≤ #result-text ‚Äî –≤–µ—Ä–Ω—ë–º –µ–≥–æ
  if (mediaWrap.parentNode !== textWrap) {
    textWrap.prepend(mediaWrap);
  }

  // –∫–∞—Ä—Ç–∏–Ω–∫–∞
  if (imageSrc) {
    mediaWrap.hidden = false;
    img.loading = 'eager';
    img.referrerPolicy = 'no-referrer';
    img.src = encodeURI(imageSrc);

    // –Ω–∞ iOS –∏–Ω–æ–≥–¥–∞ –Ω—É–∂–Ω–∞ ¬´–≤—Å—Ç—Ä—è—Å–∫–∞¬ª –¥–ª—è –ø–æ–≤—Ç–æ—Ä–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏
    img.onerror = () => {
      img.src = encodeURI(imageSrc + (imageSrc.includes('?') ? '&' : '?') + 'v=' + Date.now());
    };

    img.style.display = 'block';
    cap.textContent = caption || "";
    return;
  }

  // –≤–∏–¥–µ–æ
  if (videoSrc) {
    mediaWrap.hidden = false;
    video.src = videoSrc;
    video.style.display = 'block';
    cap.textContent = caption || "";
  }
}

